package org.geoserver.security.jwtheaders.username;

import java.text.ParseException;
import org.geoserver.security.jwtheaders.JwtConfiguration;
import org.junit.Assert;
import org.junit.Test;

public class JwtHeaderUserNameExtractorTest {

    public JwtHeaderUserNameExtractor getExtractor(JwtConfiguration.UserNameHeaderFormat format, String path) {
        JwtConfiguration config = new JwtConfiguration();
        config.setUserNameFormatChoice(format);
        config.setUserNameJsonPath(path);
        return new JwtHeaderUserNameExtractor(config);
    }

    public static String accessToken =
            "eyJhbGciOiJSUzI1NiIsInR5cCIgOiAiSldUIiwia2lkIiA6ICItWEdld190TnFwaWRrYTl2QXNJel82WEQtdnJmZDVyMlNWTWkwcWMyR1lNIn0..fHzXd7oISnqWb09ah9wikfP2UOBeiOA3vd_aDg3Bw-xcfv9aD3CWhAK5FUDPYSPyj4whAcknZbUgUzcm0qkaI8V_aS65F3Fug4jt4nC9YPL4zMSJ5an4Dp6jlQ3OQhrKFn4FwaoW61ndMmScsZZWEQyj6gzHnn5cknqySB26tVydT6q57iTO7KQFcXRdbXd6GWIoFGS-ud9XzxQMUdNfYmsDD7e6hoWhe9PJD9Zq4KT6JN13hUU4Dos-Z5SBHjRa6ieHoOe9gqkjKyA1jT1NU42Nqr-mTV-ql22nAoXuplpvOYc5-09-KDDzSDuVKFwLCNMN3ZyRF1wWuydJeU-gOQ";

    @Test
    public void testSimpleJwt() throws ParseException {
        String username = getExtractor(JwtConfiguration.UserNameHeaderFormat.JWT, "preferred_username")
                .extractUserName(accessToken);
        Assert.assertEquals("david.blasby@geocat.net", username);
    }

    String json =
            "{\"exp\":1707155912,\"iat\":1707155612,\"jti\":\"888715ae-a79d-4633-83e5-9b97dee02bbc\",\"iss\":\"https://login-live-dev.geocat.live/realms/dave-test2\",\"aud\":\"account\",\"sub\":\"ea33e3cc-f0e1-4218-89cb-8d48c27eee3d\",\"typ\":\"Bearer\",\"azp\":\"live-key2\",\"session_state\":\"ae7796fa-b374-4754-a294-e0eb834b23b5\",\"acr\":\"1\",\"realm_access\":{\"roles\":[\"default-roles-dave-test2\",\"offline_access\",\"uma_authorization\"]},\"resource_access\":{\"live-key2\":{\"roles\":[\"GeoserverAdministrator\"]},\"account\":{\"roles\":[\"manage-account\",\"manage-account-links\",\"view-profile\"]}},\"scope\":\"openidprofileemail\",\"sid\":\"ae7796fa-b374-4754-a294-e0eb834b23b5\",\"email_verified\":false,\"name\":\"davidblasby\",\"preferred_username\":\"david.blasby@geocat.net\",\"given_name\":\"david\",\"family_name\":\"blasby\",\"email\":\"david.blasby@geocat.net\"}";

    @Test
    public void testSimpleJson() throws ParseException {
        String username = getExtractor(JwtConfiguration.UserNameHeaderFormat.JSON, "preferred_username")
                .extractUserName(json);
        Assert.assertEquals("david.blasby@geocat.net", username);
    }

    @Test
    public void testSimpleString() throws ParseException {
        String json = "david.blasby@geocat.net";
        String username = getExtractor(JwtConfiguration.UserNameHeaderFormat.STRING, "xxxyyy")
                .extractUserName(json);
        Assert.assertEquals("david.blasby@geocat.net", username);
    }

    @Test
    public void testNonExistentClaim() {
        String claimValue = getExtractor(JwtConfiguration.UserNameHeaderFormat.JSON, "notThere")
                .extractUserName(json);
        Assert.assertNull(claimValue);

        claimValue = getExtractor(JwtConfiguration.UserNameHeaderFormat.JSON, "resource_access.notThere.abc")
                .extractUserName(json);
        Assert.assertNull(claimValue);

        claimValue = getExtractor(JwtConfiguration.UserNameHeaderFormat.JSON, "resource_access.live-key2.notThere")
                .extractUserName(json);
        Assert.assertNull(claimValue);

        claimValue = getExtractor(
                        JwtConfiguration.UserNameHeaderFormat.JSON, "resource_access.live-key2.roles.notThere")
                .extractUserName(json);
        Assert.assertNull(claimValue);
    }
}
